import React, { useState, useEffect } from 'react';

function App() {
  const [currentPage, setCurrentPage] = useState('login'); // 'login', 'home', 'myorders'
  const [currentUser, setCurrentUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [myOrders, setMyOrders] = useState([]);
  const [orderResult, setOrderResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch restaurants on mount
  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    try {
      const response = await fetch('/api/restaurants');
      if (!response.ok) throw new Error('Failed to fetch restaurants');
      const data = await response.json();
      setRestaurants(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const viewMenu = async (restaurant) => {
    setSelectedRestaurant(restaurant);
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`/api/menu/${restaurant.id}`);
      if (!response.ok) throw new Error('Failed to fetch menu');
      const data = await response.json();
      setMenu(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const addToCart = (item) => {
    const existing = cart.find(c => c.id === item.id);
    if (existing) {
      setCart(cart.map(c => 
        c.id === item.id ? { ...c, quantity: c.quantity + 1 } : c
      ));
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
  };

  const removeFromCart = (itemId) => {
    setCart(cart.filter(c => c.id !== itemId));
  };

  const updateQuantity = (itemId, delta) => {
    setCart(cart.map(c => {
      if (c.id === itemId) {
        const newQty = c.quantity + delta;
        return newQty > 0 ? { ...c, quantity: newQty } : c;
      }
      return c;
    }).filter(c => c.quantity > 0));
  };

  const login = async (email) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/restaurants');
      if (!response.ok) throw new Error('Failed to connect to server');
      
      // Simple login - find user by email
      const user = { id: 1, name: 'Alice Johnson', email: email };
      setCurrentUser(user);
      setCurrentPage('home');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    setCurrentUser(null);
    setCurrentPage('login');
    setCart([]);
    setSelectedRestaurant(null);
  };

  const fetchMyOrders = async () => {
    if (!currentUser) return;
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`/api/orders/${currentUser.id}`);
      if (!response.ok) throw new Error('Failed to fetch orders');
      const data = await response.json();
      setMyOrders(data);
      setCurrentPage('myorders');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const checkout = async () => {
    if (cart.length === 0) {
      alert('Cart is empty!');
      return;
    }
    
    setLoading(true);
    setError(null);
    try {
      const orderData = {
        user_id: currentUser.id,
        restaurant_id: selectedRestaurant.id,
        items: cart.map(c => ({ item_id: c.id, quantity: c.quantity }))
      };
      
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(orderData)
      });
      
      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.error || 'Failed to place order');
      }
      
      const data = await response.json();
      setOrderResult(data);
      setCart([]);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const cartTotal = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  // Login Page
  if (currentPage === 'login') {
    return (
      <div style={styles.container}>
        <div style={styles.loginContainer}>
          <h1>Food Delivery App</h1>
          <h2>Login</h2>
          <form onSubmit={(e) => { e.preventDefault(); login(e.target.email.value); }}>
            <input 
              type="email" 
              name="email" 
              placeholder="Enter your email" 
              required 
              style={styles.input}
              defaultValue="alice@example.com"
            />
            <button type="submit" style={styles.loginButton} disabled={loading}>
              {loading ? 'Logging in...' : 'Login'}
            </button>
          </form>
          {error && <div style={styles.error}>{error}</div>}
          <p style={styles.hint}>Try: alice@example.com, bob@example.com, charlie@example.com</p>
        </div>
      </div>
    );
  }

  // My Orders Page
  if (currentPage === 'myorders') {
    return (
      <div style={styles.container}>
        <header style={styles.header}>
          <h1>Food Delivery App</h1>
          <div style={styles.nav}>
            <button onClick={() => setCurrentPage('home')} style={styles.navBtn}>Home</button>
            <button onClick={fetchMyOrders} style={styles.navBtn}>My Orders</button>
            <span style={styles.userName}>üë§ {currentUser?.name}</span>
            <button onClick={logout} style={styles.navBtn}>Logout</button>
          </div>
        </header>

        <div style={styles.ordersContainer}>
          <h2>My Orders</h2>
          {loading ? (
            <p>Loading orders...</p>
          ) : myOrders.length === 0 ? (
            <p style={styles.emptyOrders}>No orders yet. Start ordering!</p>
          ) : (
            <div style={styles.ordersList}>
              {myOrders.map(order => (
                <div key={order.id} style={styles.orderCard}>
                  <div style={styles.orderHeader}>
                    <h3>Order #{order.id}</h3>
                    <span style={{
                      ...styles.statusBadge,
                      background: order.status === 'delivered' ? '#4CAF50' : 
                                  order.status === 'confirmed' ? '#2196F3' :
                                  order.status === 'cancelled' ? '#f44336' : '#FF9800'
                    }}>
                      {order.status.toUpperCase()}
                    </span>
                  </div>
                  <p><strong>Restaurant:</strong> Restaurant #{order.restaurant_id}</p>
                  <p><strong>Total:</strong> ${order.total_price.toFixed(2)}</p>
                  <p><strong>Date:</strong> {new Date(order.created_at).toLocaleString()}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }

  // Main Home Page
  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1>üçï Food Delivery App</h1>
        <div style={styles.nav}>
          <button onClick={() => setCurrentPage('home')} style={styles.navBtn}>Home</button>
          <button onClick={fetchMyOrders} style={styles.navBtn}>My Orders</button>
          <span style={styles.userName}>üë§ {currentUser?.name}</span>
          <button onClick={logout} style={styles.navBtn}>Logout</button>
        </div>
      </header>

      {error && (
        <div style={styles.error}>
          ‚ùå {error}
        </div>
      )}

      {orderResult && (
        <div style={styles.success}>
          ‚úÖ Order #{orderResult.order_id} placed successfully! 
          Total: ${orderResult.total_price.toFixed(2)}
          <button onClick={() => setOrderResult(null)} style={styles.closeBtn}>√ó</button>
        </div>
      )}

      <div style={styles.main}>
        <div style={styles.leftPanel}>
          {!selectedRestaurant ? (
            <>
              <h2>Restaurants</h2>
              <div style={styles.restaurantList}>
                {restaurants.map(restaurant => (
                  <div key={restaurant.id} style={styles.card}>
                    <h3>{restaurant.name}</h3>
                    <p>{restaurant.cuisine_type}</p>
                    <p style={styles.small}>{restaurant.address}</p>
                    <p style={styles.small}>üìû {restaurant.phone}</p>
                    <button 
                      onClick={() => viewMenu(restaurant)} 
                      style={styles.button}
                    >
                      View Menu
                    </button>
                  </div>
                ))}
              </div>
            </>
          ) : (
            <>
              <button 
                onClick={() => { setSelectedRestaurant(null); setMenu([]); }} 
                style={styles.backButton}
              >
                ‚Üê Back to Restaurants
              </button>
              <h2>{selectedRestaurant.name} - Menu</h2>
              {loading ? (
                <p>Loading menu...</p>
              ) : (
                <div style={styles.menuList}>
                  {menu.map(item => (
                    <div key={item.id} style={styles.card}>
                      <h3>{item.name}</h3>
                      <p>{item.description}</p>
                      <p style={styles.price}>${item.price.toFixed(2)}</p>
                      <button 
                        onClick={() => addToCart(item)} 
                        style={styles.button}
                      >
                        Add to Cart
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </>
          )}
        </div>

        <div style={styles.cartPanel}>
          <h2>üõí Cart</h2>
          {cart.length === 0 ? (
            <p style={styles.emptyCart}>Your cart is empty</p>
          ) : (
            <>
              {cart.map(item => (
                <div key={item.id} style={styles.cartItem}>
                  <div>
                    <strong>{item.name}</strong>
                    <div style={styles.small}>
                      ${item.price.toFixed(2)} √ó {item.quantity} = ${(item.price * item.quantity).toFixed(2)}
                    </div>
                  </div>
                  <div style={styles.quantityControls}>
                    <button onClick={() => updateQuantity(item.id, -1)} style={styles.qtyBtn}>‚àí</button>
                    <span style={styles.qtyDisplay}>{item.quantity}</span>
                    <button onClick={() => updateQuantity(item.id, 1)} style={styles.qtyBtn}>+</button>
                    <button onClick={() => removeFromCart(item.id)} style={styles.removeBtn}>üóëÔ∏è</button>
                  </div>
                </div>
              ))}
              <div style={styles.cartTotal}>
                <strong>Total: ${cartTotal.toFixed(2)}</strong>
              </div>
              <button 
                onClick={checkout} 
                style={styles.checkoutButton}
                disabled={loading}
              >
                {loading ? 'Processing...' : 'Checkout'}
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

